float CBasicMathFloat::Addition(float x, float y)
{
   return (x + y);
}

float CBasicMathFloat::Multiply(float x, float y)
{
   return (x * y);
}

float CBasicMathFloat::Substraction(float x, float y)
{
   return (x - y);
}

float CBasicMathFloat::Divide(float x, float y)
{
   return (x / y);
}


